from stallion.client.runtime import ClientRuntime

class StaticRuntime(ClientRuntime):
    """ Sort-of a stub runtime object used in places where we do not need to
        pull anything from the environment, named because it pairs well with
        the StaticConfigLoader.
    """
    def __init__(self,
            name=None,
            project_id=None,
            component_name=None,
            component_version=None,
            service_port=None,
            server_name=None):

        self._name = name
        self._project_id = project_id
        self._component_name = component_name
        self._component_version = component_version
        self._service_port = service_port
        self._server_name = server_name


    @property
    def name(self):
        return self._name

    @property
    def project_id(self):
        return self._project_id

    @property
    def component_name(self):
        return self._component_name

    @property
    def component_version(self):
        return self._component_version

    @property
    def service_port(self):
        return self._service_port

    @property
    def server_name(self):
        return self._server_name
