from builtins import object
from stallion.config.backend import default_config_backend_conf

class Project(object):
    """ A class for holding the configuration of a Stallion project """
    @property
    def id(self):
        return self._id

    @property
    def config_backend(self):
        return self._config_backend or default_config_backend_conf(self.id)

    @property
    def components(self):
        return self._components

    @property
    def errors(self):
        return self._errors

    def __init__(self, id, config_backend, components, errors):
        self._id = id
        self._components = components
        self._errors = errors
        self._config_backend = config_backend
