import marshmallow as ma
from .base import StrictSchema

class DataLakeMetadataSchema(StrictSchema):
    hive_metastore_instance = ma.fields.String(required=True)
    metastore_proxy_port = ma.fields.Integer(required=True)
    use_cloud_sql_private_ip = ma.fields.Boolean(required=True)
    kms_key_uri = ma.fields.String(required=True)
    db_hive_user = ma.fields.String(required=True)
    db_hive_password_uri = ma.fields.String(required=True)

class DataLakePropertiesSchema(StrictSchema):
    hive_metastore_warehouse_dir = ma.fields.String(required=True)
    spark_sql_sources_partitionOverwriteMode = ma.fields.String(required=True)
