import marshmallow as ma
from stallion.components.base import BaseCodeDeployComponent
from .base import BaseServiceSchema

class DataprocLogsSchema(BaseServiceSchema):
    peer_project_ids = ma.fields.List(ma.fields.String)
    historyserver_project_id = ma.fields.String()

class DataprocLogs(BaseCodeDeployComponent):
    type = 'dataproc-log-proxy'
    schema = DataprocLogsSchema
    allowed_deployment_types = frozenset([ 'gce' ])
