from stallion.components.base import BaseComponent
from stallion_etsy_plugin.components.base import BaseServiceSchema

class GCEAuthSidecar(BaseComponent):
    """ A component class for stand-alone GCE auth sidecars, without a code
        deploy.  This is used by Jenkins, for which the code is deployed via
        terraform and the auth proxy sidecar reads its necessary configs/secrets
        out of the unversioned Jenkins configuration in RuntimeConfigurator.
    """
    type = 'gce_auth_sidecar'
    schema = BaseServiceSchema
    allowed_deployment_types = frozenset([ 'noop' ])
