import marshmallow as ma
from stallion.schemas.base import StrictSchema
from stallion_etsy_plugin.components.generic_cloud_function import GenericCloudFunction

class SnapshotExecutorSchema(StrictSchema):
    schema_registry_domain_name = ma.fields.String(required=True)

# TODO inject urls to services as configuration values
class SnapshotExecutor(GenericCloudFunction):
    schema = SnapshotExecutorSchema
    type = 'snapshot-executor'


